import React, { useState } from 'react'
import axios from 'axios';
import {useParams,useNavigate,Link} from 'react-router-dom';

export default () => {
    //keep track of what is being typed via useState hook
    const [name, setName] = useState(""); 
    const [url, setUrl] = useState(""); 
    const [numberOfChest, setNumberOfChest] = useState(""); 
    const [catchPhrase, setCatchPhrase] = useState(""); 
    const [crewPosition, setCrewPosition] = useState(""); 
    const [hasPegLeg, setHasPegLeg] = useState(""); 
    const [hasEyePatch, setHasEyePatch] = useState(""); 
    const [hasHookHand, setHasHookHand] = useState(""); 
    const navigate = useNavigate();
    const [errors,setErrors] = useState([])
    const [isValid,setIsValid] = useState(false);
    //handler when the form is submitted
    const onSubmitHandler = e => {
        //prevent default behavior of the submit
        //make a post request to create a new person

        axios.post('http://localhost:8000/pirate', {
            name,
            url,
            numberOfChest,
            catchPhrase,
            crewPosition,
            hasPegLeg,
            hasEyePatch,
            hasHookHand
        })
        .then(res => navigate('/pirates')) // If successful, do something with the response. 
            .catch(err=>{
                const errorResponse = err.response.data.errors; // Get the errors from err.response.data
                const errorArr = []; // Define a temp error array to push the messages in
                for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                    errorArr.push(errorResponse[key].message)
                }
                // Set Errors
                setErrors(errorArr)
            })
            .then(validateFormElement)
    }           
    const validateFormElement = () => {
        if(name.length>2 && name.length<17) {
            setIsValid(true);
        } else {
            setIsValid(false);
        }
        return isValid;
    }
    const onChangeName = (e) => {
        setName(e.target.value)
    }
    const onChangeUrl = (e) => {
        setUrl(e.target.value)
    }
    const onChangeNumberOfChest = (e) => {
        setNumberOfChest(e.target.value)
    }
    const onChangeCatchPhrase = (e) => {
        setCatchPhrase(e.target.value)
    }
    const onChangeCrewPosition = (e) => {
        setCrewPosition(e.target.value)
    }
    const onChangeHasPegLeg = (e) => {
        setHasPegLeg(e.target.value)
    }
    const onChangeHasEyePatch = (e) => {
        setHasEyePatch(e.target.value)
    }
    const onChangeHasHookHand = (e) => {
        setHasHookHand(e.target.value)
    }
    const home = e => {
        navigate('/pirates')
    }

    return (
        <>
             {errors.map((err, index) => <div key={index}>{<p>{err}</p>}</div>)}

             <p>
                <label>Name</label><br/>
                <input type="text" onChange={(e)=>onChangeName(e)} value={name}/>
            </p>
            <p>
                <label>Image URL</label><br/>
                <input type="text" onChange={(e)=>onChangeUrl(e)} value={url}/>
            </p>
            <p>
                <label>Number Of Chest</label><br/>
                <input type="text" onChange={(e)=>onChangeNumberOfChest(e)} value={numberOfChest}/>
            </p>
            <p>
                <label>Catch Phrase</label><br/>
                <input type="text" onChange={(e)=>onChangeCatchPhrase(e)} value={catchPhrase}/>
            </p>
            <p>
                <label>Crew Position</label><br/>
                <input type="text" onChange={(e)=>onChangeCrewPosition(e)} value={crewPosition}/>
            </p>
            <p>
                <label>Peg Leg</label><br/>
                <input type="text" onChange={(e)=>onChangeHasPegLeg(e)} value={hasPegLeg}/>
            </p>
            <p>
                <label>Eye Patch</label><br/>
                <input type="text" onChange={(e)=>onChangeHasEyePatch(e)} value={hasEyePatch}/>
            </p>
            <p>
                <label>Hook Hand</label><br/>
                <input type="text" onChange={(e)=>onChangeHasHookHand(e)} value={hasHookHand}/>
            </p>
            <button onClick={home}>Cancel</button>
            <button onClick={onSubmitHandler} disabled={!isValid}>Submit</button>

        </>
    )
    }
