import React from 'react'
import axios from 'axios'
import {useParams,useNavigate,Link} from 'react-router-dom';

const PirateList = (props) => {
    const { removeFromDom } = props;
    const navigate = useNavigate();


    const style = {
        "border":"1px solid black",
        "width":"15%",
    }

    const deletePirate = (pirateId) => {
        axios.delete('http://localhost:8000/pirate/' + pirateId)
            .then(res => {
                removeFromDom(pirateId)
            })
            .catch(err => console.error(err));
    }
    const editPirate = (pirateId) => {
        navigate('/pirate/' + pirateId + "/edit")
    }
    const sortedPiratesList = props.pirates.sort(function (prev, next) {
        return prev.name.localeCompare( next.name );
    });
    console.log(props.pirates[0].name)
    return (
        <div>
            {sortedPiratesList.map( (pirate, i) =>
                <div key={i}>
                <table style={style}>
                    <thead style={{"background":"darkgrey","color":"white"}}>
                        <tr >
                            <td >Pirate Name</td>
                            <td>Actions available</td>
                        </tr>
                    </thead>
                    <tbody>
                        <tr style={{"alignItems":"space-evenly"}}>
                            <td><p style={{"color":"blue"}}>{pirate.name}</p><p><img style={{"width":"50%"}} src={pirate.url}></img></p></td>

                            <td><p><button onClick={(e)=>{editPirate(pirate._id)}}>Edit</button> 
                                <button onClick={(e)=>{deletePirate(pirate._id)}}>Delete</button>&nbsp;
                            </p></td>

                        </tr>
                    </tbody>
                </table>
                </div>

            )}
        </div>
    )

}
export default PirateList;
