import React, { useEffect, useState } from 'react'
import axios from 'axios';
import PiratesForm from '../components/PiratesForm';
import { useParams } from 'react-router-dom';

const View = (props) => {
    const [pirates, setPirates] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const {id} = useParams();
    useEffect(()=>{
        axios.get('http://localhost:8000/pirate/' + id)
            .then(res=>{
                setPirates(res.data);
                setLoaded(true);
            })
            .catch(err => console.error(err));
    },[]);

    return (
        <div style={{"width":"50%","marginLeft":"10%", "display":"flex", "flexDirection":"row"}}>
            <div id='left'>
                <p>{pirates.name}</p>
                <img src={pirates.url} style={{"width":"20%"}}/>
                <p>{pirates.catchPhrase}</p>
            </div>
            <div id='right'>
                <p>{pirates.numberOfChest}</p>
                <p>{pirates.crewPosition}</p>
                <p>{pirates.hasPegLeg}</p>
                <p>{pirates.hasEyePatch}</p>
                <p>{pirates.hasHookHand}</p>
            </div>
           </div>
    )
}

export default View;
